{
    "$schema": "http://json-schema.org/draft-06/schema#",
    "title": "Orders",
    "description": "Order object model",
    "type": "object",
    "required": [
        "amount"
    ],
    "additionalProperties": false,
    "definitions": {
        "address": {
            "type": "object",
            "additionalProperties": false,
            "required": [
                "zip"
            ],
            "properties": {
                "zip": {
                    "type": "string",
                    "maxLength": 30,
                    "description": "ZIP (CEP, postal...) code"
                },
                "street": {
                    "type": "string",
                    "maxLength": 200,
                    "description": "Street or public place name"
                },
                "number": {
                    "type": "integer",
                    "min": 1,
                    "max": 9999999,
                    "description": "House or building street number"
                },
                "complement": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "Address complement or second line, such as apartment number"
                },
                "borough": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "Borough name"
                },
                "near_to": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "Some optional other reference for this address"
                },
                "line_address": {
                    "type": "string",
                    "maxLength": 255,
                    "description": "Full in line mailing address, should include street, number and borough"
                },
                "city": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "City name"
                },
                "country": {
                    "type": "string",
                    "maxLength": 50,
                    "description": "Country name"
                },
                "country_code": {
                    "type": "string",
                    "minLength": 2,
                    "maxLength": 2,
                    "pattern": "^[A-Z]+$",
                    "description": "An ISO 3166-2 country code"
                },
                "province": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "Province or state name"
                },
                "province_code": {
                    "type": "string",
                    "minLength": 2,
                    "maxLength": 2,
                    "pattern": "^[A-Z]+$",
                    "description": "The two-letter code for the province or state"
                },
                "name": {
                    "type": "string",
                    "maxLength": 70,
                    "description": "The name of recipient, generally is the customer's name"
                },
                "last_name": {
                    "type": "string",
                    "maxLength": 70,
                    "description": "The recipient's last name"
                },
                "phone": {
                    "type": "object",
                    "additionalProperties": false,
                    "required": [
                        "number"
                    ],
                    "properties": {
                        "country_code": {
                            "type": "integer",
                            "min": 1,
                            "max": 999,
                            "description": "Country calling code (without +), defined by standards E.123 and E.164"
                        },
                        "number": {
                            "type": "string",
                            "maxLength": 19,
                            "pattern": "^[0-9]+$",
                            "description": "The actual phone number, digits only"
                        }
                    },
                    "description": "Customer phone number for this mailing address"
                }
            },
            "description": "Address object"
        }
    },
    "properties": {
        "checkout_link": {
            "type": "string",
            "maxLength": 255,
            "format": "uri",
            "description": "URL to checkout page, if necessary, use (_id) where order unique ID must appear at URL"
        },
        "checkout_mobile_link": {
            "type": "string",
            "maxLength": 255,
            "format": "uri",
            "description": "Optional checkout URL specific to mobile devices"
        },
        "status_link": {
            "type": "string",
            "maxLength": 255,
            "format": "uri",
            "description": "URL to order status page, if necessary, use (_id) where order unique ID must appear"
        },
        "status_mobile_link": {
            "type": "string",
            "maxLength": 255,
            "format": "uri",
            "description": "Optional status URL specific to mobile devices"
        },
        "utm": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "source": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "Parameter \"utm_source\", the referrer: (e.g. google, newsletter)"
                },
                "medium": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "Parameter \"utm_medium\", the marketing medium: (e.g. cpc, banner, email)"
                },
                "campaign": {
                    "type": "string",
                    "maxLength": 200,
                    "description": "Parameter \"utm_campaign\", the product, promo code, or slogan (e.g. spring_sale)"
                },
                "term": {
                    "type": "string",
                    "maxLength": 100,
                    "description": "Parameter \"utm_term\", identifies the paid keywords"
                },
                "content": {
                    "type": "string",
                    "maxLength": 255,
                    "description": "Parameter \"utm_content\", used to differentiate ads"
                }
            },
            "description": "UTM campaign HTTP parameters"
        },
        "referring_site": {
            "type": "string",
            "maxLength": 255,
            "format": "uri",
            "description": "The website that the customer clicked on to come to the shop"
        },
        "affiliate_code": {
            "type": "string",
            "maxLength": 200,
            "description": "Code to identify the affiliate that referred the customer"
        },
        "source_name": {
            "type": "string",
            "maxLength": 30,
            "default": "Web",
            "description": "Where the order originated"
        },
        "browser_ip": {
            "type": "string",
            "maxLength": 30,
            "format": "ipv4",
            "description": "IP address of the browser used by the customer when placing the order"
        },
        "channel_id": {
            "type": "integer",
            "min": 10000,
            "max": 4294967295,
            "description": "Channel unique identificator"
        },
        "channel_type": {
            "type": "string",
            "maxLength": 20,
            "enum": [
                "ecommerce",
                "mobile",
                "pdv",
                "button",
                "facebook",
                "chatbot"
            ],
            "default": "ecommerce",
            "description": "Channel type or source"
        },
        "domain": {
            "type": "string",
            "minLength": 4,
            "maxLength": 100,
            "pattern": "^[0-9a-z-.]+$",
            "description": "Store domain name (numbers and lowercase letters, eg.: www.myshop.sample)"
        },
        "number": {
            "type": "integer",
            "min": 1,
            "max": 999999999,
            "description": "Order number, if not specified will be filled auto incrementing"
        },
        "code": {
            "type": "string",
            "maxLength": 70,
            "description": "Order code used by seller and customer for identification, should be unique"
        },
        "status": {
            "type": "string",
            "enum": [
                "open",
                "closed",
                "cancelled"
            ],
            "default": "open",
            "description": "Order main status"
        },
        "opened_at": {
            "type": "string",
            "format": "date-time",
            "description": "Date and time when order was opened, in ISO 8601 standard representation"
        },
        "closed_at": {
            "type": "string",
            "format": "date-time",
            "description": "If the order is closed, specify the date and time it occurred"
        },
        "cancelled_at": {
            "type": "string",
            "format": "date-time",
            "description": "If the order is cancelled, specify the date and time it occurred"
        },
        "cancel_reason": {
            "type": "string",
            "enum": [
                "customer",
                "fraud",
                "inventory",
                "declined",
                "other"
            ],
            "description": "If the order is cancelled, specify the reason why it was cancelled"
        },
        "seller_status": {
            "type": "string",
            "maxLength": 50,
            "description": "Order status created by seller"
        },
        "financial_status": {
            "type": "object",
            "additionalProperties": false,
            "required": [
                "current"
            ],
            "properties": {
                "updated_at": {
                    "type": "string",
                    "format": "date-time",
                    "description": "Last status change, date and time in ISO 8601 standard representation"
                },
                "current": {
                    "type": "string",
                    "enum": [
                        "pending",
                        "under_analysis",
                        "authorized",
                        "partially_paid",
                        "paid",
                        "in_dispute",
                        "partially_refunded",
                        "refunded",
                        "voided",
                        "unknown"
                    ],
                    "description": "Order financial status"
                }
            },
            "description": "Financial status and date of change"
        },
        "fulfillment_status": {
            "type": "object",
            "additionalProperties": false,
            "required": [
                "current"
            ],
            "properties": {
                "updated_at": {
                    "type": "string",
                    "format": "date-time",
                    "description": "Last status change, date and time in ISO 8601 standard representation"
                },
                "current": {
                    "type": "string",
                    "enum": [
                        "invoice_issued",
                        "in_production",
                        "in_separation",
                        "ready_for_shipping",
                        "partially_shippend",
                        "shipped",
                        "partially_delivered",
                        "delivered",
                        "returned_for_exchange",
                        "received_for_exchange",
                        "returned"
                    ],
                    "description": "Order fulfillment (shipping) status"
                }
            },
            "description": "Fulfillment status and date of change"
        },
        "currency_id": {
            "type": "string",
            "pattern": "^[A-Z]{3}$",
            "default": "BRL",
            "description": "Designator of currency according to ISO 4217 (3 uppercase letters)"
        },
        "currency_symbol": {
            "type": "string",
            "maxLength": 20,
            "default": "R$",
            "description": "Graphic symbol used as a shorthand for currency's name"
        },
        "amount": {
            "type": "object",
            "additionalProperties": false,
            "required": [
                "total"
            ],
            "properties": {
                "total": {
                    "type": "number",
                    "multipleOf": 0.00001,
                    "minimum": 0,
                    "maximum": 9999999999,
                    "description": "Order total amount"
                },
                "subtotal": {
                    "type": "number",
                    "multipleOf": 0.00001,
                    "minimum": 0,
                    "maximum": 9999999999,
                    "description": "The sum of all items prices"
                },
                "freight": {
                    "type": "number",
                    "multipleOf": 0.00001,
                    "minimum": 0,
                    "maximum": 9999999999,
                    "description": "Order freight cost"
                },
                "discount": {
                    "type": "number",
                    "multipleOf": 0.00001,
                    "minimum": 0,
                    "maximum": 9999999999,
                    "description": "Applied discount value"
                },
                "tax": {
                    "type": "number",
                    "multipleOf": 0.00001,
                    "minimum": 0,
                    "maximum": 9999999999,
                    "description": "The sum of all the taxes applied to the order"
                },
                "extra": {
                    "type": "number",
                    "multipleOf": 0.00001,
                    "minimum": 0,
                    "maximum": 9999999999,
                    "description": "Sum of optional extra costs applied"
                }
            },
            "description": "Object with sums of values"
        },
        "payment_method_label": {
            "type": "string",
            "maxLength": 100,
            "description": "Name or summary of order payment method(s)"
        },
        "shipping_method_label": {
            "type": "string",
            "maxLength": 100,
            "description": "Name of order shipping method(s)"
        },
        "buyers": {
            "type": "array",
            "maxItems": 20,
            "items": {
                "type": "object",
                "additionalProperties": false,
                "required": [
                    "_id"
                ],
                "properties": {
                    "_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "Customer ID"
                    },
                    "locale": {
                        "type": "string",
                        "pattern": "^[a-z]{2}(_[a-z]{2})?$",
                        "description": "Customer language two letter code, sometimes with region, eg.: pt_br, fr, en_us"
                    },
                    "main_email": {
                        "type": "string",
                        "maxLength": 200,
                        "format": "email",
                        "description": "Customer main email address"
                    },
                    "emails": {
                        "type": "array",
                        "maxItems": 20,
                        "items": {
                            "type": "object",
                            "additionalProperties": false,
                            "required": [
                                "address"
                            ],
                            "properties": {
                                "address": {
                                    "type": "string",
                                    "maxLength": 200,
                                    "format": "email",
                                    "description": "The actual email address"
                                },
                                "type": {
                                    "type": "string",
                                    "enum": [
                                        "work",
                                        "home",
                                        "other"
                                    ],
                                    "description": "The type of email"
                                },
                                "verified": {
                                    "type": "boolean",
                                    "default": false,
                                    "description": "States whether or not the email address has been verified"
                                }
                            },
                            "description": "Email object"
                        },
                        "description": "List of customer email addresses"
                    },
                    "display_name": {
                        "type": "string",
                        "maxLength": 50,
                        "description": "The name of this Customer, suitable for display"
                    },
                    "name": {
                        "type": "object",
                        "additionalProperties": false,
                        "properties": {
                            "family_name": {
                                "type": "string",
                                "maxLength": 70,
                                "description": "The family name of this user, or \"last name\""
                            },
                            "given_name": {
                                "type": "string",
                                "maxLength": 70,
                                "description": "The \"first name\" of this user"
                            },
                            "middle_name": {
                                "type": "string",
                                "maxLength": 255,
                                "description": "The middle name(s) of this user"
                            }
                        },
                        "description": "Customer name object"
                    },
                    "gender": {
                        "type": "string",
                        "enum": [
                            "f",
                            "m",
                            "x"
                        ],
                        "description": "Customer gender, female, male or third gender (X)"
                    },
                    "photos": {
                        "type": "array",
                        "uniqueItems": true,
                        "maxItems": 20,
                        "items": {
                            "type": "string",
                            "maxLength": 255,
                            "description": "Image URL"
                        },
                        "description": "User profile pictures"
                    },
                    "phones": {
                        "type": "array",
                        "maxItems": 20,
                        "items": {
                            "type": "object",
                            "additionalProperties": false,
                            "required": [
                                "number"
                            ],
                            "properties": {
                                "country_code": {
                                    "type": "integer",
                                    "min": 1,
                                    "max": 999,
                                    "description": "Country calling code (without +), defined by standards E.123 and E.164"
                                },
                                "number": {
                                    "type": "string",
                                    "maxLength": 19,
                                    "pattern": "^[0-9]+$",
                                    "description": "The actual phone number, digits only"
                                },
                                "type": {
                                    "type": "string",
                                    "enum": [
                                        "home",
                                        "personal",
                                        "work",
                                        "other"
                                    ],
                                    "description": "The type of phone"
                                }
                            },
                            "description": "Phone object"
                        },
                        "description": "List of customer phone numbers"
                    },
                    "registry_type": {
                        "type": "string",
                        "enum": [
                            "p",
                            "j"
                        ],
                        "description": "Physical or juridical (company) person"
                    },
                    "doc_country": {
                        "type": "string",
                        "minLength": 2,
                        "maxLength": 2,
                        "pattern": "^[A-Z]+$",
                        "description": "Country of document origin, an ISO 3166-2 code"
                    },
                    "doc_number": {
                        "type": "string",
                        "maxLength": 19,
                        "pattern": "^[0-9]+$",
                        "description": "Responsible person or organization document number (only numbers)"
                    },
                    "inscription_type": {
                        "type": "string",
                        "enum": [
                            "State",
                            "Municipal"
                        ],
                        "description": "Municipal or state registration if exists"
                    },
                    "inscription_number": {
                        "type": "string",
                        "maxLength": 50,
                        "description": "Municipal or state registration number (with characters) if exists"
                    },
                    "corporate_name": {
                        "type": "string",
                        "maxLength": 255,
                        "description": "Registered company name or responsible fullname"
                    }
                },
                "description": "Customer object"
            },
            "description": "List of order customers"
        },
        "gift_to": {
            "type": "array",
            "maxItems": 20,
            "items": {
                "type": "object",
                "additionalProperties": false,
                "required": [
                    "_id"
                ],
                "properties": {
                    "_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "Customer ID"
                    },
                    "locale": {
                        "type": "string",
                        "pattern": "^[a-z]{2}(_[a-z]{2})?$",
                        "description": "Customer language two letter code, sometimes with region, eg.: pt_br, fr, en_us"
                    },
                    "main_email": {
                        "type": "string",
                        "maxLength": 200,
                        "format": "email",
                        "description": "Customer main email address"
                    },
                    "emails": {
                        "type": "array",
                        "maxItems": 20,
                        "items": {
                            "type": "object",
                            "additionalProperties": false,
                            "required": [
                                "address"
                            ],
                            "properties": {
                                "address": {
                                    "type": "string",
                                    "maxLength": 200,
                                    "format": "email",
                                    "description": "The actual email address"
                                },
                                "type": {
                                    "type": "string",
                                    "enum": [
                                        "work",
                                        "home",
                                        "other"
                                    ],
                                    "description": "The type of email"
                                },
                                "verified": {
                                    "type": "boolean",
                                    "default": false,
                                    "description": "States whether or not the email address has been verified"
                                }
                            },
                            "description": "Email object"
                        },
                        "description": "List of customer email addresses"
                    },
                    "display_name": {
                        "type": "string",
                        "maxLength": 50,
                        "description": "The name of this Customer, suitable for display"
                    },
                    "name": {
                        "type": "object",
                        "additionalProperties": false,
                        "properties": {
                            "family_name": {
                                "type": "string",
                                "maxLength": 70,
                                "description": "The family name of this user, or \"last name\""
                            },
                            "given_name": {
                                "type": "string",
                                "maxLength": 70,
                                "description": "The \"first name\" of this user"
                            },
                            "middle_name": {
                                "type": "string",
                                "maxLength": 255,
                                "description": "The middle name(s) of this user"
                            }
                        },
                        "description": "Customer name object"
                    },
                    "gender": {
                        "type": "string",
                        "enum": [
                            "f",
                            "m",
                            "x"
                        ],
                        "description": "Customer gender, female, male or third gender (X)"
                    },
                    "photos": {
                        "type": "array",
                        "uniqueItems": true,
                        "maxItems": 20,
                        "items": {
                            "type": "string",
                            "maxLength": 255,
                            "description": "Image URL"
                        },
                        "description": "User profile pictures"
                    },
                    "phones": {
                        "type": "array",
                        "maxItems": 20,
                        "items": {
                            "type": "object",
                            "additionalProperties": false,
                            "required": [
                                "number"
                            ],
                            "properties": {
                                "country_code": {
                                    "type": "integer",
                                    "min": 1,
                                    "max": 999,
                                    "description": "Country calling code (without +), defined by standards E.123 and E.164"
                                },
                                "number": {
                                    "type": "string",
                                    "maxLength": 19,
                                    "pattern": "^[0-9]+$",
                                    "description": "The actual phone number, digits only"
                                },
                                "type": {
                                    "type": "string",
                                    "enum": [
                                        "home",
                                        "personal",
                                        "work",
                                        "other"
                                    ],
                                    "description": "The type of phone"
                                }
                            },
                            "description": "Phone object"
                        },
                        "description": "List of customer phone numbers"
                    },
                    "gifts_list_info": {
                        "type": "object",
                        "additionalProperties": false,
                        "properties": {
                            "title": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Title for the gifts list, generally defined by the customer"
                            },
                            "description": {
                                "type": "string",
                                "maxLength": 255,
                                "description": "Short text description for the gifts list"
                            },
                            "permalink": {
                                "type": "string",
                                "maxLength": 255,
                                "format": "uri",
                                "description": "URL to gifts list page"
                            },
                            "event_date": {
                                "type": "string",
                                "format": "date-time",
                                "description": "Date and time of the event in ISO 8601 standard representation"
                            },
                            "valid_thru": {
                                "type": "string",
                                "format": "date-time",
                                "description": "Validity of the list, until this date the gifts can be bought"
                            },
                            "authors": {
                                "type": "array",
                                "uniqueItems": true,
                                "maxItems": 10,
                                "items": {
                                    "type": "string",
                                    "maxLength": 70,
                                    "description": "Author name"
                                },
                                "description": "Authors of the list, if it is a wedding list, the names of the bride and groom"
                            },
                            "notification_email": {
                                "type": "string",
                                "maxLength": 200,
                                "format": "email",
                                "description": "Email address to be notified after gift purchase"
                            },
                            "contact": {
                                "type": "string",
                                "maxLength": 200,
                                "description": "Email address or phone number to contact list owners"
                            }
                        },
                        "description": "Informations about this customer gifts list"
                    }
                },
                "description": "Customer object"
            },
            "description": "If order is originated from a gifts list, specify customers who will receive the gift"
        },
        "budget_only": {
            "type": "boolean",
            "default": false,
            "description": "Whether the order is only budget, without payment transactions"
        },
        "shipping_lines": {
            "type": "array",
            "maxItems": 100,
            "items": {
                "type": "object",
                "additionalProperties": false,
                "required": [
                    "_id",
                    "from",
                    "to"
                ],
                "properties": {
                    "_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "Unique ID (ObjectID)"
                    },
                    "from": {
                        "$ref": "#/definitions/address",
                        "description": "Sender's address"
                    },
                    "to": {
                        "$ref": "#/definitions/address",
                        "description": "Shipping address (recipient)"
                    },
                    "package": {
                        "type": "object",
                        "additionalProperties": false,
                        "properties": {
                            "dimensions": {
                                "type": "object",
                                "additionalProperties": false,
                                "patternProperties": {
                                    "^width|height|length$": {
                                        "type": "object",
                                        "required": [
                                            "value"
                                        ],
                                        "additionalProperties": false,
                                        "properties": {
                                            "value": {
                                                "type": "number",
                                                "multipleOf": 0.001,
                                                "minimum": 0,
                                                "maximum": 999999,
                                                "description": "Size in specified unit"
                                            },
                                            "unit": {
                                                "type": "string",
                                                "enum": [
                                                    "mm",
                                                    "cm",
                                                    "m",
                                                    "ft",
                                                    "in",
                                                    "yd",
                                                    "mi"
                                                ],
                                                "default": "cm",
                                                "description": "Unit of measurement"
                                            }
                                        },
                                        "description": "Package width, height and length"
                                    }
                                },
                                "description": "Package dimensions"
                            },
                            "weight": {
                                "type": "object",
                                "required": [
                                    "value"
                                ],
                                "additionalProperties": false,
                                "properties": {
                                    "value": {
                                        "type": "number",
                                        "multipleOf": 0.001,
                                        "minimum": 0,
                                        "maximum": 999999,
                                        "description": "Size in specified unit"
                                    },
                                    "unit": {
                                        "type": "string",
                                        "enum": [
                                            "mg",
                                            "g",
                                            "kg",
                                            "lb",
                                            "oz"
