{
    "$schema": "http://json-schema.org/draft-06/schema#",
    "title": "Products",
    "description": "Product object model",
    "type": "object",
    "required": [
        "sku",
        "name"
    ],
    "additionalProperties": false,
    "properties": {
        "authorized_stores": {
            "type": "array",
            "uniqueItems": true,
            "maxItems": 1000,
            "items": {
                "type": "integer",
                "minimum": 1000,
                "maximum": 16777215,
                "description": "Store ID"
            },
            "description": "IDs from other stores authorized to sell this product"
        },
        "sku": {
            "type": "string",
            "minLength": 2,
            "maxLength": 100,
            "pattern": "^[A-Za-z0-9-_.]+$",
            "description": "Product unique reference code"
        },
        "commodity_type": {
            "type": "string",
            "enum": [
                "physical",
                "digital"
            ],
            "default": "physical",
            "description": "Product type"
        },
        "name": {
            "type": "string",
            "maxLength": 255,
            "description": "Product full name"
        },
        "i18n": {
            "type": "object",
            "additionalProperties": false,
            "maxProperties": 10,
            "patternProperties": {
                "^[a-z]{2}(_[a-z]{2})?$": {
                    "type": "string",
                    "maxLength": 255,
                    "description": "Translated full product name"
                }
            },
            "description": "Product name translations"
        },
        "subtitle": {
            "type": "string",
            "maxLength": 255,
            "description": "Optional subtitle intended to be used for SEO or layout purposes"
        },
        "slug": {
            "type": "string",
            "maxLength": 255,
            "pattern": "^[a-z0-9]([a-z0-9-_./]+)?$",
            "description": "Slug to complete page URL, starting with number or lowercase letter"
        },
        "permalink": {
            "type": "string",
            "maxLength": 255,
            "format": "uri",
            "description": "Permanent URL to product page"
        },
        "mobile_link": {
            "type": "string",
            "maxLength": 255,
            "format": "uri",
            "description": "Optional URL specific to mobile devices"
        },
        "status": {
            "type": "string",
            "maxLength": 50,
            "description": "Status defined by seller, such as draft or live"
        },
        "available": {
            "type": "boolean",
            "default": true,
            "description": "True when product is complete registered and ready to sell"
        },
        "visible": {
            "type": "boolean",
            "default": true,
            "description": "Visible on search results, collections and catalogs"
        },
        "ad_relevance": {
            "type": "integer",
            "minimum": -128,
            "maximum": 127,
            "default": 0,
            "description": "Number to define the importance of the ad, the higher should be displayed first"
        },
        "ad_type": {
            "type": "string",
            "maxLength": 50,
            "description": "Option ad type defined by seller, such as gold and silver"
        },
        "short_description": {
            "type": "string",
            "maxLength": 255,
            "description": "Short product description in plain text"
        },
        "body_html": {
            "type": "string",
            "maxLength": 500000,
            "description": "Full product description, may use HTML tags"
        },
        "body_text": {
            "type": "string",
            "maxLength": 500000,
            "description": "Full product description, plain text only"
        },
        "meta_title": {
            "type": "string",
            "maxLength": 255,
            "description": "Title tag for page SEO"
        },
        "meta_description": {
            "type": "string",
            "maxLength": 1000,
            "description": "Meta description tag for page SEO"
        },
        "keywords": {
            "type": "array",
            "uniqueItems": true,
            "maxItems": 200,
            "items": {
                "type": "string",
                "maxLength": 50,
                "description": "Term (keyword)"
            },
            "description": "Tags related with product for internal search"
        },
        "price": {
            "type": "number",

            "minimum": 0,
            "maximum": 999999999,
            "description": "Product sale price, when undefined, product could be bought by any price"
        },
        "price_effective_date": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "start": {
                    "type": "string",
                    "format": "date-time",
                    "description": "Start date and time in ISO 8601 standard representation"
                },
                "end": {
                    "type": "string",
                    "format": "date-time",
                    "description": "Promotion end date and time in ISO 8601 standard representation"
                }
            },
            "description": "How long sale price is valid, out of this range (if defined), base_price should be applied"
        },
        "base_price": {
            "type": "number",

            "minimum": 0,
            "maximum": 999999999,
            "description": "Product original sale price, without discounts"
        },
        "cost_price": {
            "type": "number",

            "minimum": 0,
            "maximum": 999999999,
            "description": "Product cost price to calculate profit"
        },
        "currency_id": {
            "type": "string",
            "pattern": "^[A-Z]{3}$",
            "default": "BRL",
            "description": "Designator of currency according to ISO 4217 (3 uppercase letters)"
        },
        "currency_symbol": {
            "type": "string",
            "maxLength": 20,
            "default": "R$",
            "description": "Graphic symbol used as a shorthand for currency's name"
        },
        "quantity": {
            "type": "number",

            "minimum": 0,
            "maximum": 9999999,
            "description": "Product quantity available to sell, max to put in cart"
        },
        "min_quantity": {
            "type": "number",

            "minimum": 0,
            "maximum": 999999,
            "description": "Minimum quantity to put in cart"
        },
        "production_time": {
            "type": "object",
            "required": [
                "days"
            ],
            "additionalProperties": false,
            "properties": {
                "days": {
                    "type": "integer",
                    "minimum": 0,
                    "maximum": 99999,
                    "description": "Number of days to post the product after purchase, deadline for production or handling"
                },
                "working_days": {
                    "type": "boolean",
                    "default": true,
                    "description": "If the deadline is calculated in working days"
                },
                "cumulative": {
                    "type": "boolean",
                    "default": false,
                    "description": "If the production time is cumulative per product unit"
                },
                "max_time": {
                    "type": "integer",
                    "minimum": 0,
                    "maximum": 99999,
                    "description": "When cumulative, increase the term proportionally up to this maximum (in days)"
                }
            },
            "description": "Deadline for production or handling of pre-ordered product"
        },
        "manage_stock": {
            "type": "boolean",
            "default": true,
            "description": "Decrease product available quantity automatically after each order"
        },
        "inventory_records": {
            "type": "array",
            "maxItems": 180,
            "items": {
                "type": "object",
                "required": [
                    "_id",
                    "date_of_change",
                    "quantity"
                ],
                "additionalProperties": false,
                "properties": {
                    "_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "Unique ID (ObjectID)"
                    },
                    "date_of_change": {
                        "type": "string",
                        "format": "date-time",
                        "description": "Date and time when stock changed in ISO 8601 standard representation"
                    },
                    "origin": {
                        "type": "string",
                        "maxLength": 50,
                        "description": "Optional note about origin or reason of stock changing"
                    },
                    "quantity": {
                        "type": "number",

                        "minimum": -9999999,
                        "maximum": 9999999,
                        "description": "Added or decreased (negative) stock quantity"
                    },
                    "variation_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "ID to specify the variation, if the stock was changed from a variation"
                    }
                },
                "description": "Historical record"
            },
            "description": "Recent stock history"
        },
        "measurement": {
            "type": "object",
            "required": [
                "unit"
            ],
            "additionalProperties": false,
            "properties": {
                "unit": {
                    "type": "string",
                    "enum": [
                        "oz",
                        "lb",
                        "mg",
                        "g",
                        "kg",
                        "floz",
                        "pt",
                        "qt",
                        "gal",
                        "ml",
                        "cl",
                        "l",
                        "cbm",
                        "in",
                        "ft",
                        "yd",
                        "cm",
                        "m",
                        "sqft",
                        "sqm",
                        "ct"
                    ],
                    "description": "Unit of measurement"
                },
                "precision": {
                    "type": "integer",
                    "minimum": 0,
                    "maximum": 4,
                    "description": "Maximum number of decimal places in the product quantity (in unit of measure)"
                },
                "pricing_base_measure": {
                    "type": "number",

                    "minimum": 0,
                    "maximum": 9999999,
                    "description": "Quantity to calculate the price to be shown on ads, such as GMC unit_pricing_base_measure"
                }
            },
            "description": "Measure and dimension of your product when users might be interested in price per unit"
        },
        "multipack": {
            "type": "integer",
            "minimum": 0,
            "maximum": 999999,
            "description": "Indicate that this item is a group of multiple identical products"
        },
        "dimensions": {
            "type": "object",
            "additionalProperties": false,
            "patternProperties": {
                "^width|height|length$": {
                    "type": "object",
                    "required": [
                        "value"
                    ],
                    "additionalProperties": false,
                    "properties": {
                        "value": {
                            "type": "number",

                            "minimum": 0,
                            "maximum": 999999,
                            "description": "Size in specified unit"
                        },
                        "unit": {
                            "type": "string",
                            "enum": [
                                "mm",
                                "cm",
                                "m",
                                "ft",
                                "in",
                                "yd",
                                "mi"
                            ],
                            "default": "cm",
                            "description": "Unit of measurement"
                        }
                    },
                    "description": "Package width, height and length"
                }
            },
            "description": "Product dimensions with packaging for freight calculation"
        },
        "weight": {
            "type": "object",
            "required": [
                "value"
            ],
            "additionalProperties": false,
            "properties": {
                "value": {
                    "type": "number",

                    "minimum": 0,
                    "maximum": 999999,
                    "description": "Size in specified unit"
                },
                "unit": {
                    "type": "string",
                    "enum": [
                        "mg",
                        "g",
                        "kg",
                        "lb",
                        "oz"
                    ],
                    "default": "g",
                    "description": "Unit of measurement"
                }
            },
            "description": "Package weight for freight calculation"
        },
        "condition": {
            "type": "string",
            "enum": [
                "new",
                "refurbished",
                "used",
                "not_specified"
            ],
            "default": "new",
            "description": "Item condition"
        },
        "adult": {
            "type": "boolean",
            "default": false,
            "description": "Product restricted to adults (18+)"
        },
        "warranty": {
            "type": "string",
            "maxLength": 100,
            "description": "Warranty time and conditions"
        },
        "brands": {
            "type": "array",
            "maxItems": 100,
            "items": {
                "type": "object",
                "additionalProperties": false,
                "required": [
                    "_id",
                    "name"
                ],
                "properties": {
                    "_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "Brand ID"
                    },
                    "name": {
                        "type": "string",
                        "maxLength": 255,
                        "description": "Brand name"
                    },
                    "slug": {
                        "type": "string",
                        "maxLength": 255,
                        "pattern": "^[a-z0-9]([a-z0-9-_./]+)?$",
                        "description": "Brand page slug"
                    },
                    "logo": {
                        "type": "object",
                        "required": [
                            "url"
                        ],
                        "additionalProperties": false,
                        "properties": {
                            "url": {
                                "type": "string",
                                "maxLength": 255,
                                "format": "uri",
                                "description": "Image link"
                            },
                            "size": {
                                "type": "string",
                                "maxLength": 11,
                                "pattern": "^[1-9]([0-9]+)?x[1-9]([0-9]+)?$",
                                "description": "Image size (width x height) in px, such as 100x50 (100px width, 50px height)"
                            },
                            "alt": {
                                "type": "string",
                                "maxLength": 255,
                                "description": "Alternative text, HTML alt tag (important for SEO)"
                            }
                        },
                        "description": "Brand logo"
                    }
                },
                "description": "Product brand (manufacturer)"
            },
            "description": "List of product brands"
        },
        "categories": {
            "type": "array",
            "maxItems": 100,
            "items": {
                "type": "object",
                "additionalProperties": false,
                "required": [
                    "_id",
                    "name"
                ],
                "properties": {
                    "_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "Category ID"
                    },
                    "name": {
                        "type": "string",
                        "maxLength": 255,
                        "description": "Category name"
                    },
                    "slug": {
                        "type": "string",
                        "maxLength": 255,
                        "pattern": "^[a-z0-9]([a-z0-9-_./]+)?$",
                        "description": "Category page slug"
                    },
                    "ml_category_id": {
                        "type": "string",
                        "pattern": "^ML[A-Z][0-9]{2,9}$",
                        "description": "ID in Mercado Libre Taxonomy"
                    },
                    "google_product_category_id": {
                        "type": "integer",
                        "minimum": 1,
                        "maximum": 999999999999,
                        "description": "ID in Google Taxonomy"
                    }
                },
                "description": "Category object"
            },
            "description": "List of product categories"
        },
        "category_tree": {
            "type": "string",
            "maxLength": 750,
            "description": "Products main category tree, separating by >, such as \"Women > Dresses\""
        },
        "ml_category_id": {
            "type": "string",
            "pattern": "^ML[A-Z][0-9]{2,9}$",
            "description": "Preferential category of the product in the Mercado Libre Taxonomy"
        },
        "google_product_category_id": {
            "type": "integer",
            "minimum": 1,
            "maximum": 999999999999,
            "description": "Preferential category of the product in the Google Products Taxonomy"
        },
        "grids": {
            "type": "object",
            "additionalProperties": false,
            "maxProperties": 200,
            "patternProperties": {
                "^[a-z0-9_]{2,30}$": {
                    "type": "string",
                    "maxLength": 70,
                    "description": "Grid title"
                }
            },
            "description": "Grids for specifications and variations, grid_id as property name, such as gender or colors"
        },
        "specifications": {
            "type": "object",
            "additionalProperties": false,
            "maxProperties": 200,
            "properties": {
                "energy_efficiency_class": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Text displayed for the client describing the specification (custom value)"
                            },
                            "value": {
                                "type": "string",
                                "enum": [
                                    "A+++",
                                    "A++",
                                    "A+",
                                    "A",
                                    "B",
                                    "C",
                                    "D",
                                    "E",
                                    "F",
                                    "G"
                                ],
                                "description": "Value in the Google Product Specification"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Attribute to tell users the energy efficiency class of your product"
                },
                "age_group": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Text displayed for the client describing the specification (custom value)"
                            },
                            "value": {
                                "type": "string",
                                "enum": [
                                    "newborn",
                                    "infant",
                                    "toddler",
                                    "kids",
                                    "adult"
                                ],
                                "description": "Value in the Google Product Specification"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Attribute to set the demographic that your product is designed for"
                },
                "gender": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Text displayed for the client describing the specification (custom value)"
                            },
                            "value": {
                                "type": "string",
                                "enum": [
                                    "male",
                                    "female",
                                    "unisex"
                                ],
                                "description": "Value in the Google Product Specification"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Specify for what gender your product is designed"
                },
                "material": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 200,
                                "description": "Text displayed for the client describing the attribute"
                            },
                            "value": {
                                "type": "string",
                                "maxLength": 200,
                                "description": "Default normalized value to use on GMC and other integrations"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Attribute to describe the main fabric or material that your product is made of"
                },
                "pattern": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 200,
                                "description": "Text displayed for the client describing the attribute"
                            },
                            "value": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Default normalized value to use on GMC and other integrations"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Attribute to describe the pattern or graphic printed on your product"
                },
                "size": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 200,
                                "description": "Text displayed for the client describing the attribute"
                            },
                            "value": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Default normalized value to use on GMC and other integrations"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Attribute to describe the standardized size of your product"
                },
                "size_type": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Text displayed for the client describing the attribute"
                            },
                            "value": {
                                "type": "string",
                                "enum": [
                                    "regular",
                                    "petite",
                                    "plus",
                                    "big and tall",
                                    "maternity"
                                ],
                                "description": "Value in the Google Product Specification"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Attribute to describe the cut of your product"
                },
                "size_system": {
                    "type": "array",
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 100,
                                "description": "Text displayed for the client describing the attribute"
                            },
                            "value": {
                                "type": "string",
                                "enum": [
                                    "AU",
                                    "BR",
                                    "CN",
                                    "DE",
                                    "EU",
                                    "FR",
                                    "IT",
                                    "JP",
                                    "MEX",
                                    "UK",
                                    "US"
                                ],
                                "description": "Value in the Google Product Specification"
                            }
                        },
                        "description": "Specification object"
                    },
                    "description": "Which country's sizing system your product uses"
                },
                "colors": {
                    "type": "array",
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "additionalProperties": false,
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 50,
                                "description": "Color name"
                            },
                            "value": {
                                "type": "string",
                                "pattern": "^#[a-f0-9]{6}$",
                                "description": "RGB code with #"
                            }
                        },
                        "description": "Color object"
                    },
                    "description": "Product color palette, list ordered by importance, starting by main color"
                }
            },
            "patternProperties": {
                "^[a-z0-9_]{2,30}$": {
                    "type": "array",
                    "minItems": 1,
                    "maxItems": 30,
                    "items": {
                        "type": "object",
                        "required": [
                            "text"
                        ],
                        "additionalProperties": false,
                        "properties": {
                            "text": {
                                "type": "string",
                                "maxLength": 255,
                                "description": "Text displayed for the client describing the specification"
                            },
                            "value": {
                                "type": "string",
                                "maxLength": 200,
                                "description": "Default normalized value to use in integrations"
                            },
                            "external_values": {
                                "type": "object",
                                "additionalProperties": false,
                                "maxProperties": 5,
                                "patternProperties": {
                                    "^[a-z0-9_]{2,30}$": {
                                        "type": "string",
                                        "maxLength": 200,
                                        "description": "Attribute text external value"
                                    }
                                },
                                "description": "Normalized values to use in specific integrations (ID in property)"
                            }
                        },
                        "description": "Attribute object"
                    },
                    "description": "Custom product attributes, grid_id as property name"
                }
            },
            "description": "Product attributes and technical specifications"
        },
        "pictures": {
            "type": "array",
            "maxItems": 50,
            "items": {
                "type": "object",
                "required": [
                    "_id",
                    "normal"
                ],
                "additionalProperties": false,
                "properties": {
                    "_id": {
                        "type": "string",
                        "pattern": "^[a-f0-9]{24}$",
                        "description": "Unique ID (ObjectID)"
                    },
                    "tag": {
                        "type": "string",
                        "maxLength": 20,
                        "pattern": "^[a-z0-9_]+$",
                        "description": "Tag to identify object, use only lowercase letters, digits and underscore"
                    }
                },
                "patternProperties": {
                    "^small|normal|big|zoom|custom$": {
                        "type": "object",
                        "required": [
                            "url"
                        ],
                        "additionalProperties": false,
                        "properties": {
                            "url": {
                                "type": "string",
                                "maxLength": 255,
                                "format": "uri",
                                "description": "Image link"
                            },
                            "size": {
                                "type": "string",
                                "maxLength": 11,
                                "pattern": "^[1-9]([0-9]+)?x[1-9]([0-9]+)?$",
                                "description": "Image size (width x height) in px, such as 100x50 (100px width, 50px height)"
                            },
                            "alt": {
                                "type": "string",
                                "maxLength": 255,
                                "description": "Alternative text, HTML alt tag (important for SEO)"
                            }
                        },
                        "description": "Image size variation"
                    }
                },
                "description": "Image object"
            },
            "description": "List of product images"
        },
        "videos": {
            "type": "array",
            "maxItems": 30,
            "items": {
                "type": "object",
