            "type": "array",
            "uniqueItems": true,
            "maxItems": 10,
            "items": {
                "type": "string",
                "maxLength": 20,
                "description": "Flag title"
            },
            "description": "Flags to associate additional info"
        },
        "notes": {
            "type": "string",
            "maxLength": 255,
            "description": "Optional notes with additional info about this procedure"
        }
    }
